-- =====================================================
-- THERMOTECH-OPS 完整初始化腳本（含任務綁定）
-- 79 位員工 + 98 個任務（根據 Excel 預設綁定）
-- =====================================================

-- ============ 第 1 步：清空並匯入 79 位員工 ============
DELETE FROM public.daily_assignments;
DELETE FROM public.task_definitions;
DELETE FROM public.profiles;


-- ============ 第 2 步：加入密碼欄位 ============
ALTER TABLE public.profiles ADD COLUMN IF NOT EXISTS password TEXT DEFAULT 'Ops2025!';
UPDATE public.profiles SET password = 'Admin369888' WHERE employee_id IN ('70231', '70250', 'A0001');

-- ============ 第 3 步：匯入 98 個任務（根據 Excel 預設綁定）============

-- 重置 ID 序列，從 1 開始
ALTER SEQUENCE task_definitions_id_seq RESTART WITH 1;


-- ============ 第 4 步：驗證 ============
SELECT 
  (SELECT COUNT(*) FROM public.profiles) as total_users,
  (SELECT COUNT(*) FROM public.profiles WHERE role = 'admin') as admin_users,
  (SELECT COUNT(*) FROM public.task_definitions) as total_tasks,
  (SELECT COUNT(*) FROM public.task_definitions WHERE default_assignee_id IS NOT NULL) as tasks_with_assignee;

-- 檢查關鍵員工
SELECT employee_id, full_name, role, password FROM public.profiles WHERE employee_id IN ('70037', '70231', '70250', 'A0001') ORDER BY employee_id;